$.fn.rowLinkTo=function(options){var defaults={restPath:'',idSelector:'input[type=hidden]'};options=$.extend({}, defaults, options);if(options.restPath!==undefined || options.restPath.length>0){this.children('tr').each(function(){$(this).click(function(){document.location=options.restPath+$(this).find(options.idSelector).val();});});}else{console.error("Failure in rowLinkTo: options.restPath is empty or undefined.");}};